package jbasic;

public class RelationOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=50,b=10;
		
		
		System.out.println("a is > b"+ (a>b));
		System.out.println("a is < b"+ (a<b));
		System.out.println("a is >= b"+ (a>=b));
		System.out.println("a is <= b"+ (a<=b));
		System.out.println("a is == b"+ (a==b));
		System.out.println("a is != b"+ (a!=b));
		
		
	}

}
